import httpStatus from 'http-status';
import { s3Service, emailTemplateService } from 'services';
import { catchAsync } from 'utils/catchAsync';
import FileFieldValidationEnum from 'models/fileFieldValidation.model';
import mongoose from 'mongoose';
import TempS3 from 'models/tempS3.model';
import { asyncForEach } from 'utils/common';
import { pick } from '../../utils/pick';

const moveFileAndUpdateTempS3 = async ({ url, newFilePath }) => {
  const newUrl = await s3Service.moveFile({ key: url, newFilePath });
  await TempS3.findOneAndUpdate({ url }, { url: newUrl });
  return newUrl;
};
// this is used to move file to new specified path as shown in basePath, used in create and update controller.
const moveFiles = async ({ body, user, moveFileObj }) => {
  await asyncForEach(Object.keys(moveFileObj), async (key) => {
    const fieldValidation = FileFieldValidationEnum[`${key}OfEmailTemplate`];
    const basePath = `users/${user._id}/emailTemplate/${body._id}/${key}/${mongoose.Types.ObjectId()}/`;
    if (Array.isArray(moveFileObj[key])) {
      const newUrlsArray = [];
      moveFileObj[key].map(async (ele) => {
        const filePath = `${mongoose.Types.ObjectId()}_${ele.split('/').pop()}`;
        newUrlsArray.push(await moveFileAndUpdateTempS3({ url: ele, newFilePath: basePath + filePath }));
      });
      Object.assign(body, { ...body, [key]: await Promise.all(newUrlsArray) });
    } else {
      const filePath = `${mongoose.Types.ObjectId()}_${moveFileObj[key].split('/').pop()}`;
      Object.assign(body, {
        ...body,
        [key]: await moveFileAndUpdateTempS3({
          url: moveFileObj[key],
          newFilePath: basePath + filePath,
        }),
      });
      if (fieldValidation.generateThumbnails) {
        Object.assign(body, {
          ...body,
          [`thumbOf${key}`]: await s3Service.createThumbnails({
            url: moveFileObj[key],
            resolutions: fieldValidation.thumbnailResolutions,
          }),
        });
      }
    }
  });
};
export const get = catchAsync(async (req, res) => {
  const { emailTemplateId } = req.params;
  const filter = {
    _id: emailTemplateId,
  };
  const options = {};
  const emailTemplate = await emailTemplateService.getOne(filter, options);
  return res.status(httpStatus.OK).send({ results: emailTemplate });
});

export const list = catchAsync(async (req, res) => {
  const filter = {};
  const options = {};
  const emailTemplate = await emailTemplateService.getEmailTemplateList(filter, options);
  return res.status(httpStatus.OK).send({ results: emailTemplate });
});

export const paginate = catchAsync(async (req, res) => {
  const { query } = req;
  const sortingObj = pick(query, ['sort', 'order']);
  const sortObj = {
    [sortingObj.sort]: sortingObj.order,
  };
  const filter = {};
  const options = {
    ...pick(query, ['limit', 'page']),
  };
  if (sortingObj.sort) {
    options.sort = sortObj;
  }
  const emailTemplate = await emailTemplateService.getEmailTemplateListWithPagination(filter, options);
  emailTemplate.results = emailTemplate.results.map((emailTemplateObject) => ({
    createdAt: emailTemplateObject.createdAt,
    ...emailTemplateObject.toJSON(),
  }));
  return res.status(httpStatus.OK).send({ results: emailTemplate });
});

export const create = catchAsync(async (req, res) => {
  const { body } = req;
  body.createdBy = req.user;
  body.updatedBy = req.user;
  const { user } = req;
  const moveFileObj = {
    ...(body.emailAttachments && { emailAttachments: body.emailAttachments }),
  };
  body._id = mongoose.Types.ObjectId();
  await moveFiles({ body, user, moveFileObj });
  const options = {};
  const emailTemplateResult = await emailTemplateService.createEmailTemplate(body, options);
  if (emailTemplateResult) {
    const uploadedFileUrls = [];
    uploadedFileUrls.push(emailTemplateResult.emailAttachments);
    await TempS3.updateMany({ url: { $in: uploadedFileUrls } }, { active: true });
  }
  return res.status(httpStatus.CREATED).send({ results: emailTemplateResult });
});

export const update = catchAsync(async (req, res) => {
  const { body } = req;
  body.updatedBy = req.user;
  const { emailTemplateId } = req.params;
  const { user } = req;
  const moveFileObj = {
    ...(body.emailAttachments && { emailAttachments: body.emailAttachments }),
  };
  body._id = emailTemplateId;
  await moveFiles({ body, user, moveFileObj });
  const filter = {
    _id: emailTemplateId,
  };
  const options = { new: true };
  const emailTemplateResult = await emailTemplateService.updateEmailTemplate(filter, body, options);
  // tempS3
  if (emailTemplateResult) {
    const uploadedFileUrls = [];
    uploadedFileUrls.push(emailTemplateResult.emailAttachments);
    await TempS3.updateMany({ url: { $in: uploadedFileUrls } }, { active: true });
  }
  return res.status(httpStatus.OK).send({ results: emailTemplateResult });
});

export const remove = catchAsync(async (req, res) => {
  const { emailTemplateId } = req.params;
  const filter = {
    _id: emailTemplateId,
  };
  const emailTemplate = await emailTemplateService.removeEmailTemplate(filter);
  return res.status(httpStatus.OK).send({ results: emailTemplate });
});
